import './Register.css'
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Card, Logo, Form, Input, Button, Error, Success} from '../AuthForm';
import logoImg from "../../assets/logo.png";
import validator from 'validator'

const Register = ()=> {
    const [errorText, setErrorText]= useState("")
    const [successText, setSuccessText]= useState("")
    const [fullname, setFullname] = useState("");
    const [email, setEmail] = useState("");
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const register = () =>{
        if (
            fullname!="" &&
            email!="" &&
            username!="" &&
            password!="" &&
            confirmPassword !=""
          ){
            if(validator.isEmail(email)){
                if (password === confirmPassword) {

                }else{
                    setErrorText('The passwords you entered do not match');
                }
            }else{
                setErrorText('Please enter a valid email');  
            }
          }else{
              setErrorText('Please complete all of the fields')
          }


    }
  return(
      <div>
        <Card>
        <Logo src={logoImg} />
        <Form>
            <Input 
                type="fullname" 
                value={fullname}
                onChange={e => {
                setFullname(e.target.value);
                }}
                placeholder="Fullname"
            />
            <Input
                type="username"
                value={username}
                onChange={e => {
                setUsername(e.target.value);
                }}
                placeholder="Username" 
            />
            <Input 
                type="email" 
                value={email}
                onChange={e => {
                setEmail(e.target.value);
                }}
                placeholder="Email" 
            />
            <Input 
                type="password" 
                value={password}
                onChange={e => {
                setPassword(e.target.value);
                }}
                placeholder="Password" 
            />
            <Input 
                type="password" 
                value={confirmPassword}
                onChange={e => {
                setConfirmPassword(e.target.value);
                }}
                placeholder="Confirm Password" 
            />
            <Button onClick={()=>register()}>Register</Button>
        </Form>
        <Link to="/">Already have an account?</Link>
        { errorText!="" &&setSuccessText("") &&<Error>{errorText}</Error> }
        { successText!="" && setErrorText("")&&<Success>{successText}</Success> }
        </Card>
      </div>
  )
}

export default Register
