import axios from 'axios';

const apiclient= axios.create({
    baseURL: 'http://localhost:8005'
});

export const postLogin = (username, password, setErrorText)=>{
    apiclient.post("/login", {
        username,
        password
      }).then(result => {

      }).catch(e => {
        setErrorText(e.response.data.detail)
      });
}

export const postRegister = (fullname, username, email, password, setErrorText, setSuccessText)=>{
    apiclient.post("/register", {
        fullname, 
        username, 
        email, 
        password
      }).then(result => {
        if(result.status==201){
            setSuccessText("Account Successfully! \n Please go and login")
        }
      }).catch(e => {
        setErrorText(e.response.data.detail)
      });
}
