import './chat.css'
import 'react-chatbox-component/dist/style.css';
import React, { useState, useEffect } from "react";
import { ChatBox } from 'react-chatbox-component';

function Chat() {
  const CHATBOT_AVATAR_URL = "https://images.idgesg.net/images/article/2018/02/artificial_intelligence_ai_virtual_assistant_robot_chatbot_thinkstock_856909876-100749925-large.jpg"
  const USER_AVATAR_URL = "https://upload.wikimedia.org/wikipedia/commons/thumb/f/f9/OOjs_UI_icon_userAvatar-constructive.svg/1024px-OOjs_UI_icon_userAvatar-constructive.svg.png"
  const [messageHistory, setMessageHistory] = useState([])
  const [ws, setWs] = useState(null)
  const [wordClassifications, setWordClassifications] = useState([])
  const currnetUser = {
    "uid": "user1"
  }

  useEffect(() => {
    let ws = new WebSocket("ws://localhost:8000/bot");
    ws.onopen = () => {
      console.log("connected websocket main component");
      setWs(ws)
    };
    ws.onclose = e => {
      console.log(
        'Socket is closed.', e.reason
      );
    }
  }, [])

  if (ws) {
    ws.onmessage = (event) => {
      let botMsg = createFullMessage(event.data, 'chatbot', 'chatbot1', CHATBOT_AVATAR_URL)
      updateChat(botMsg)
    };
  }

  const createFullMessage = (text, name, uid, avatar_url) => {
    return {
      "text": text,
      "id": messageHistory.length + 1,
      "sender": {
        "name": name,
        "uid": uid,
        "avatar": avatar_url,
      },
    }
  }

  const submitMessage = (text, name, uid, avatar_url) => {
    requestClassification(text)
    if (ws) {
      ws.send(text)
      let userMsg = createFullMessage(text, name, uid, avatar_url)
      updateChat(userMsg)
    }
  }

  const updateChat = (msg) => {
    let newMessageHistory = messageHistory.concat([msg])
    setMessageHistory(newMessageHistory)
  }

  const displayWordClassifications = () => {
    return (
      <div className='box'>
        <h2>Word Breakdown</h2>
        <ul style={{ listStyle: 'none', display: 'inline-block' }}>
          {wordClassifications.map((classification, index) => {
            return <li key={index}>word: {classification.word} level: {classification.level}</li>
          })}
        </ul>
      </div>

    )

  }

  const requestClassification = text => {
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ message: text })
    };
    fetch('http://localhost:8001/classify', requestOptions)
      .then(async response => {
        const data = await response.json();
        if (!response.ok) {
          const error = (data && data.message) || response.status;
          return Promise.reject(error);
        }
        let newWordClassifications = wordClassifications.concat(data)
        const seen = new Set();
        const filteredWordClassifications = newWordClassifications.filter(el => {
          const duplicate = seen.has(el.word);
          seen.add(el.word);
          return !duplicate;
        });
        setWordClassifications(filteredWordClassifications)
      })
      .catch(error => {
        console.error('Error:', error);
      });
  }

  return (
    <div className="App">
      <h1>Chinese Chatbot</h1>
      <div className='main_items_container'>
        <div className='box'>
          <h2>Grammar Breakdown</h2>
        </div>
        <div className="box">
          <ChatBox
            messages={messageHistory}
            onSubmit={(newMsg) => submitMessage(newMsg, 'currentUser', "user1", USER_AVATAR_URL)}
            isLoading={ws == null || ws.readyState == WebSocket.CLOSED}
            user={currnetUser} />
        </div>
        {displayWordClassifications()}
      </div>

    </div>
  );
}

export default Chat;
